mapper.update:
UPDATE BBS SET
TITLE=#{title}, CONTENT=#{content}, WDATE=SYSDATE
WHERE SEQ=#{seq}

UPDATE BBS
SET DEL=1
WHERE SEQ=#{seq}

mapper.select:
SELECT SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, PARENT, DEL, READCOUNT FROM (SELECT ROW_NUMBER() OVER(ORDER BY REF DESC, STEP ASC) AS RNUM, SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, PARENT, DEL, READCOUNT FROM BBS WHERE 1=1
AND TITLE LIKE '%'||#{s_keyword}||'%'
AND CONTENT LIKE '%'||#{s_keyword}||'%'
) A WHERE RNUM BETWEEN ${start} AND ${end}
